SELECT CourseOffers.CourseOfferId, Courses.CourseId, Year, Quartile, CourseName, CourseDescription, Degrees.DegreeId, ECTS, Dept, DegreeDescription, TotalECTS, Teachers.TeacherId, TeacherName, Address, BirthyearTeacher, Gender FROM CourseOffers LEFT JOIN Courses ON Courses.CourseId = CourseOffers.CourseId AND CourseOffers.CourseOfferId = 1 LEFT JOIN Degrees ON Degrees.DegreeId = Courses.DegreeId LEFT JOIN TeacherAssignmentsToCourses ON TeacherAssignmentsToCourses.CourseOfferId = CourseOffers.CourseOfferId LEFT JOIN Teachers ON Teachers.TeacherId = TeacherAssignmentsToCourses.TeacherId;
SELECT CourseOffers.CourseOfferId, CourseOffers.CourseId, Year, Quartile, Students.StudentId, StudentName, Address, BirthyearStudent, Gender, Degrees.DegreeId, Dept, DegreeDescription, TotalECTS FROM Students LEFT JOIN StudentRegistrationToDegrees ON Students.StudentId = StudentRegistrationToDegrees.StudentId AND Students.StudentId = 140 LEFT JOIN StudentAssistants ON StudentRegistrationToDegrees. StudentRegistrationId = StudentAssistants.StudentRegistrationId LEFT JOIN CourseOffers ON StudentAssistant.CourseOfferId = CourseOffers.CourseOfferId LEFT JOIN Degrees ON StudentRegistrationToDegrees.DegreeId = Degrees.DegreeId;
SELECT AVG(Grade) FROM Students LEFT JOIN StudentRegistrationsToDegrees ON Students.StudentId = StudentRegistrationsToDegrees.StudentId AND Student.StudentId = 140 LEFT JOIN CourseRegistration ON CourseRegistration.StudentRegistrationId = StudentRegistrationsToDegrees.StudentId;
